// direcciones
enum direction
{
    UP,
    DOWN,
    RIGHT,
    LEFT
};

struct playerInfo
{
    int posX, posY, avatar;
};

vector<playerInfo> players;

void readMessage(int clientSocket)
{

    char bufferRead[BUFFERSIZE];
    while (1)
    {
        // clear memory buffer
        memset(bufferRead, 0, BUFFERSIZE);
        int n = recv(clientSocket, bufferRead, sizeof(bufferRead), 0);
        bufferRead[n] = '\0';

        if (bufferRead[0] == 'M')
        {
            string posMapStr(&bufferRead[1]);
            int posMap = std::stoi(posMapStr);
            posx = posMap;
        }
        else if (bufferRead[0] == 'P')
        {
            string block(&bufferRead[1]);
            // Encontrar las posiciones de las comas
            size_t comma1Pos = block.find(',');
            size_t comma2Pos = block.find(',', comma1Pos + 1);

            // Extraer las subcadenas que representan los valores y convertirlas a enteros
            std::string valorAsciiStr = block.substr(0, comma1Pos);
            std::string posXStr = block.substr(comma1Pos + 1, comma2Pos - comma1Pos - 1);
            std::string posYStr = block.substr(comma2Pos + 1);

            int receivedValorAscii = stoi(valorAsciiStr);
            int receivedPosX = stoi(posXStr);
            int receivedPosY = stoi(posYStr);

            // Actualizar la información del jugador correspondiente en el vector
            for (int i = 0; i < players.size(); i++)
            {
                if (receivedValorAscii == players[i].avatar)
                {
                    players[i].posX = receivedPosX;
                    players[i].posY = receivedPosY;
                    break; // Romper el bucle una vez que se actualiza la posición del jugador
                }
            }
        }

        else if (bufferRead[0] == 'N')
        {
            string playerAvatar(&bufferRead[1]);
            players.push_back({naveX, naveY, stoi(playerAvatar)});
        }
        // bufferRead[n] = '\0';
        // std::cout << "\nmensaje recibido: " << bufferRead << endl;
    }
    close(clientSocket);
}

int pantalla_move(WINDOW *win, int direction, int naveYY, int naveXX)
{

    wclear(win);

    //----

    for (int y = 0; y < 26; y++)
    {
        for (int x = posx; x < 112 + posx; x++)
        {
            mvwaddch(win, y, x - posx, pantalla[y][x]);
        }
    }
    // posx++;
    // if (posx > 112)
    // posx = 0;

    // NAVE
    switch (direction)
    {
    case UP:
        naveY--;
        break;
    case DOWN:
        naveY++;
        break;
    case RIGHT:
        naveX++;
        break;
    case LEFT:
        naveX--;
        break;
    default:
        break;
    }
    playerInfo player;
    player.posX = naveX;
    player.posY = naveY;
    player.avatar = avatar;
    send(clientSocket, (char *)&player, sizeof(player), 0);

    for (int i = 0; i < players.size(); i++)
    {
        if (avatar == players[i].avatar)
        {
            players[i].posX = naveX;
            players[i].posY = naveY;
        }
        // mvwaddch(win, players[i].posY, players[i].posX, players[i].avatar);
    }
    mvwaddch(win, naveY, naveX, avatar);
    mvprintw(1, 1, "El tamaño del vector es: %zu", players.size());
    mvprintw(2, 1, "otroPos: %d, %d", otroPosX, otroPosY);
    mvwaddch(win, otroPosY, otroPosX, 'x');
    for (int i = 0; i < players.size(); i++)
    {
        mvprintw(i + 3, 1, "jugador %d: %d, %d, %d", i, players[i].posX, players[i].posY, players[i].avatar);
    }

    // mvprintw(naveY,naveX-5,"Y: %d X: %d ",naveY,naveX);

    box(win, 0, 0);
    move(0, 0);
    wrefresh(win);

    // Imprimir contenido estático

    return 0;
}

int main(int argc, char *argv[])
{

    system("clear");

    sockaddr_in serverAddr;
    serverAddr.sin_family = AF_INET;
    serverAddr.sin_port = htons(45000);
    serverAddr.sin_addr.s_addr = inet_addr("127.0.0.1");

    if (connect(clientSocket, (struct sockaddr *)&serverAddr, sizeof(serverAddr)) == -1)
    {
        std::cerr << "Error al conectar con el servidor" << std::endl;
        close(clientSocket);
        return -1;
    }

    std::cout << "Conectado al servidor" << std::endl;

    thread(readMessage, clientSocket).detach();

    // identificar cliente
    cout << "Write your nickname to get started: ";
    string nickname;
    cin >> nickname;

    send(clientSocket, nickname.c_str(), nickname.size(), 0);

    // recibir avatar de jugador (numero de cliente)
    int valorAscii;
    recv(clientSocket, &valorAscii, sizeof(valorAscii), 0);
    avatar = valorAscii;
    int playersCount = valorAscii - '0';
    for (int i = 0; i < playersCount; i++)
    {
        players.push_back({naveX, naveY, valorAscii - i});
    }

    // sleep(2);

    // crear ventana para juego
    int offsetx, offsety, ch;

    initscr();
    noecho();
    cbreak();
    timeout(TICKRATE);
    keypad(stdscr, TRUE);
    refresh();

    offsetx = (COLS - WORLD_WIDTH) / 2;
    offsety = (LINES - WORLD_HEIGHT) / 2;

    space_world = newwin(WORLD_HEIGHT, WORLD_WIDTH, offsety, offsetx);

    int cur_dir = RIGHT;

    // capturar direccion de teclado
    while ((ch = getch()) != 'x')
    {

        if (ch != ERR)
        {
            switch (ch)
            {
            case KEY_UP:
                cur_dir = UP;
                break;
            case KEY_DOWN:
                cur_dir = DOWN;
                break;
            case KEY_RIGHT:
                cur_dir = RIGHT;
                break;
            case KEY_LEFT:
                cur_dir = LEFT;
                break;
            default:
                break;
            }
        }
        // actualizar pantalla
        pantalla_move(space_world, cur_dir, naveY, naveX);
    }

    printf("Closing chat ...\n *** Finished program ***\n ");

    delwin(space_world);
    endwin();
    shutdown(clientSocket, SHUT_RDWR);
    close(clientSocket);

    return 0;
}