// posicion inicial de la pantalla
int posx = 0;

// posicion inicial de la nave
int naveX = 10;
int naveY = 13;

// direcciones
enum direction
{
    UP,
    DOWN,
    RIGHT,
    LEFT
};

// funcion para mover la pantalla
int pantalla_move(WINDOW *win, int direction, int naveY, int naveX);

int main(int argc, char *argv[])
{

    WINDOW *space_world;
    int offsetx, offsety, ch;

    initscr();
    noecho();
    cbreak();
    timeout(TICKRATE);
    keypad(stdscr, TRUE);
    refresh();

    offsetx = (COLS - WORLD_WIDTH) / 2;
    offsety = (LINES - WORLD_HEIGHT) / 2;

    space_world = newwin(WORLD_HEIGHT, WORLD_WIDTH, offsety, offsetx);

    int cur_dir = RIGHT;

    while ((ch = getch()) != 'x')
    {

        if (ch != ERR)
        {
            switch (ch)
            {
            case KEY_UP:
                cur_dir = UP;
                break;
            case KEY_DOWN:
                cur_dir = DOWN;
                break;
            case KEY_RIGHT:
                cur_dir = RIGHT;
                break;
            case KEY_LEFT:
                cur_dir = LEFT;
                break;
            default:
                break;
            }
        }
        pantalla_move(space_world, cur_dir, naveY, naveX);
    }

    delwin(space_world);

    endwin();

    return 0;
}

int pantalla_move(WINDOW *win, int direction, int naveYY, int naveXX)
{

    wclear(win);

    //----

    for (int y = 0; y < 26; y++)
    {
        for (int x = posx; x < 112 + posx; x++)
        {
            mvwaddch(win, y, x - posx, pantalla[y][x]);
        }
    }
    posx++;
    if (posx > 112)
        posx = 0;

    // NAVE
    switch (direction)
    {
    case UP:
        naveY--;
        break;
    case DOWN:
        naveY++;
        break;
    case RIGHT:
        naveX++;
        break;
    case LEFT:
        naveX--;
        break;
    default:
        break;
    }
    mvwaddch(win, naveY, naveX, '1');
    // mvprintw(naveY,naveX-5,"Y: %d X: %d ",naveY,naveX);

    box(win, 0, 0);
    move(0, 0);
    wrefresh(win);

    // Imprimir contenido estático

    box(win, 0, 0);
    move(0, 0);
    wrefresh(win);

    return 0;
}